  , USER_NO
  , KEYWORD_NO
  , KEYWORD_NAME
  , TITLE
  , CONTENTS
  , CREATE_DT
  , MODIFY_DT
) VALUES (
    BLOG_LIST_SEQ.NEXTVAL
  , #{user.userNo}
  , #{keywordNo}
  , #{keywordName}
  , #{title}
  , #{contents}
  , CURRENT_TIMESTAMP
  , CURRENT_TIMESTAMP
)


  SELECT MAX(BLOG_LIST_NO)
    FROM BLOG_DETAIL_T

