mapper.select:
SELECT USER_NO, EMAIL, PW, NAME, GENDER, MOBILE, SIGNUP_KIND, PW_MODIFY_DT, SIGNUP_DT 
  FROM USER_T

  EMAIL = #{email}
  AND PW = #{pw}

mapper.insert:
INSERT INTO USER_T (
    USER_NO
  , EMAIL
  , PW
  , NAME
  , GENDER
  , MOBILE
  , SIGNUP_KIND
  , PW_MODIFY_DT
  , SIGNUP_DT
) VALUES (
    USER_SEQ.NEXTVAL
  , #{email}
  , #{pw}
  , #{name}
  , #{gender}
  , #{mobile}
  , #{signupKind}
  , CURRENT_DATE
  , CURRENT_DATE
)

INSERT INTO ACCESS_HISTORY_T (
    ACCESS_HISTORY_NO
  , EMAIL
  , IP
  , SESSION_ID
  , SIGNIN_DT
) VALUES (
    ACCESS_HISTORY_SEQ.NEXTVAL
  , #{email}
  , #{ip}
  , #{sessionId}
  , CURRENT_DATE
)

mapper.update:
UPDATE ACCESS_HISTORY_T
   SET SESSION_ID = #{sessionId}
 WHERE SESSION_ID = #{sessionId}

mapper.select:
SELECT USER_NO, BLOG_CONTENTS, BLOG_IMG_PATH, NICKNAME
  FROM USER_T
 WHERE USER_NO = #{userNo}

SELECT USER_NO, BLOG_CONTENTS, BLOG_IMG_PATH, NICKNAME
  FROM USER_T
 WHERE USER_NO = #{userNo}

mapper.update:
UPDATE USER_T
   SET BLOG_CONTENTS = #{blogContents}
     , BLOG_IMG_PATH = #{blogImgPath, jdbcType=VARCHAR}
     , NICKNAME = #{nickname}
 WHERE USER_NO = #{userNo}

mapper.select:
SELECT BLOG_IMG_PATH
FROM USER_T
WHERE USER_NO = #{userNo}
